    public static int maxSumDistinctTriplet1(int[] x, int[] y) {
        int max=-1;
        for(int i=0;i<x.length;i++){
            for(int j=0;j<x.length;j++){
                for(int k=0;k<x.length;k++){
                    if(x[i] != x[j] && x[j] != x[k] && x[i] != x[k]){
                        max = Math.max(max,y[i]+y[j]+y[k]);
                    }
                }
            }
        }
        return max;
    }

